import express from 'express';
import bodyParser from 'body-parser';

const app = express();

// what will happen when our we restart our server , all the post urls which we used to update
// articlesInfo will be lost
// So, to avoid that problem we need a database

const articlesInfo = {
  'learn-react': {
    upvotes: 0,
    comments: [],
  },
  'learn-node': {
    upvotes: 0,
    comments: [],
  },
  'my-thoughts-on-resumes': {
    upvotes: 0,
    comments: [],
  },
};

app.get('/hello', (req, res) => {
  res.send('Hello!');
});

app.post('/api/articles/:name/upvote', (req, res) => {
  const articleName = req.params.name;

  articlesInfo[articleName].upvotes += 1;
  res
    .status(200)
    .send(`${articleName} has upvotes ${articlesInfo[articleName].upvotes} `);
});

app.post('/api/articles/:name/add-comments', (req, res) => {
  const {username, text} = req.body;
  const articleName = req.params.name;

  articlesInfo[articleName].comments.push({username, text});
  res.status(200).send(articlesInfo[articleName]);
});

app.use(bodyParser.json());

const withDB = async (operations, res) => {
  try {
    const client = await MongoClient.connect('mongodb://localhost:27017', {
      useNewUrlParser: true,
    });
    const db = client.db('my-blog');

    await operations(db);

    client.close();
  } catch (error) {
    res.status(500).json({message: 'Error connecting to db', error});
  }
};

app.get('/api/articles/:name', async (req, res) => {
  withDB(async (db) => {
    const articleName = req.params.name;

    const articleInfo = await db
      .collection('articles')
      .findOne({name: articleName});
    res.status(200).json(articleInfo);
  }, res);
});

app.post('/api/articles/:name/upvote', async (req, res) => {
  withDB(async (db) => {
    const articleName = req.params.name;

    const articleInfo = await db
      .collection('articles')
      .findOne({name: articleName});
    await db.collection('articles').updateOne(
      {name: articleName},
      {
        $set: {
          upvotes: articleInfo.upvotes + 1,
        },
      }
    );
    const updatedArticleInfo = await db
      .collection('articles')
      .findOne({name: articleName});

    res.status(200).json(updatedArticleInfo);
  }, res);
});

app.post('/api/articles/:name/add-comment', (req, res) => {
  const {username, text} = req.body;
  const articleName = req.params.name;

  withDB(async (db) => {
    const articleInfo = await db
      .collection('articles')
      .findOne({name: articleName});
    await db.collection('articles').updateOne(
      {name: articleName},
      {
        $set: {
          comments: articleInfo.comments.concat({username, text}),
        },
      }
    );
    const updatedArticleInfo = await db
      .collection('articles')
      .findOne({name: articleName});

    res.status(200).json(updatedArticleInfo);
  }, res);
});

app.listen(8000, () => {
  console.log('listening on port 8000');
});

// run npx babel-node src/server.js
// and then hit url http://localhost:8000/hello

// by using node-mon the server changes will be re -start whenever our files changes. instead of we running our server
